CREATE PROCEDURE [Accounting].[spCurrencyAuto]
	@userId int,
	@operation tinyint,
	@input [Accounting].[UdttCurrency] readonly
AS

BEGIN
DECLARE @accountId int,@accountname varchar(200),@customerId int,@customername varchar(200),@payment decimal(26,6),@createdate datetime DECLARE crs CURSOR FOR(SELECT vp.AccountId,vp.AccountName,vp.CustomerId,vp.CustomerName,vp.Deficit,vp.CreateDate FROM Accounting.VwPayment vp
join Accounting.[Contract] c on c.CustomerId=vp.CustomerId
WHERE ContractTypeId <>3 ) OPEN crs FETCH NEXT FROM crs INTO @accountId,@accountname,@customerId,@customername,@payment,@createdate WHILE @@FETCH_STATUS=0 BEGIN DECLARE @deficit decimal(26,6)=0 ,@deficit2 decimal(26,6)=0 SELECT @deficit=SUM(CASE WHEN vp.AccountId LIKE '7' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '8' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '9' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '10' AND vp.CustomerId=@customerId THEN Deficit ELSE 0 END),
@deficit2=SUM(CASE WHEN vp.AccountId LIKE '3' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '4' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '5' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '6' AND vp.CustomerId=@customerId THEN Deficit WHEN vp.AccountId LIKE '11' AND vp.CustomerId=@customerId THEN Deficit ELSE 0 END)
FROM Accounting.VwPayment vp
DECLARE @cust int=0 SELECT @cust=ISNULL(p.CustomerId,0) FROM [Accounting].CustomerDebt p WHERE p.CustomerId=@customerId IF @cust=@customerId UPDATE [Accounting].CustomerDebt SET Deficit=@deficit-@deficit2 WHERE CustomerId=@customerId;
else insert into [Accounting].CustomerDebt (CustomerId,Deficit) values(@customerId,@deficit-@deficit2)
FETCH NEXT FROM crs INTO @accountId,@accountname,@customerId,@customername,@payment,@createdate end
close crs deallocate crs SELECT * FROM [Accounting].VwCustomerDebt --SELECT CustomerName,ISNULL((@deficit-@deficit2),0) Borc FROM Accounting.VwPayment WHERE CustomerId=@customerId GROUP BY CustomerName where Deficit >=0 end

begin set xact_abort on; if @operationType in (1,11) goto spAdd; else if @operationType in (2,12) goto spEdit; else if @operationType in (3,13) goto spDelete; else if @operationType =4 goto spSelect;
spAdd: begin begin transaction insert into [Accounting].[Customer]([CustomerName],[ParentId],[TypeId] ,[CustomerLeader],[WorkTypeId],[Voen] ,[CurrencyId] ,[BankId],[BankSwift] ,[RegionId],[LegalAddress],[ActualAddress],[Email] ,[Tel],[Date],[Description],[CreateId],[DocTypeId],[DocCertId],[DocSeriesId]) values(@customerName,@parentId,@typeId,@customerLeader,@workTypeId,@voen,@currencyId,@bankId,@bankSwift,@regionId,@legalAddress,@actualAddress,@email,@tel,@date,@description,@userId,@docTypeId,@docCertId,@docSeriesId);
set @id=SCOPE_IDENTITY();
insert into [Accounting].[Contract]([CustomerId],[ContractDate],[CurrencyId],[OperationTypeId],[CompanyId],[ContractTypeId],[Description]) select @id,[ContractDate],[CurrencyId],[OperationTypeId] ,[CompanyId] ,[ContractTypeId],[Description] from @contract
if @@TRANCOUNT>0 commit;
if @operationType=11 goto spSelect; RETURN 0 end
spEdit: begin begin transaction
UPDATE [Accounting].[Customer] SET
[CustomerName] = @customerName ,[ParentId]=@parentId ,[TypeId]=@typeId ,[CustomerLeader] = @customerLeader ,[WorkTypeId] = @WorkTypeId ,[Voen] = @Voen ,[CurrencyId] = @CurrencyId ,[BankId] = @bankId ,[BankSwift]=@bankSwift ,[RegionId]=@regionId ,[LegalAddress] = @LegalAddress ,[ActualAddress] = @ActualAddress ,[Email] = @Email ,[Tel] = @Tel ,[Date] = @Date ,[Description] = @Description ,[UpdateId]=@userId ,[UpdateDate]= getdate() ,[DocTypeId]=@docTypeId ,[DocSeriesId]=@docSeriesId ,[DocCertId]=@docCertId
where Id=@id;
merge [Accounting].[Contract] target using(select [Id],@id [CustomerId],[ContractDate],[ContractTypeId],[CurrencyId],[OperationTypeId],[CompanyId],[Description] from @contract) source on target.Id=source.Id when MATCHED and target.CustomerId=@id THEN update set [CustomerId]=source.[CustomerId],[ContractDate]=source.[ContractDate],[ContractTypeId]=source.[ContractTypeId],[CurrencyId]=source.[CurrencyId],[OperationTypeId]=source.[OperationTypeId] ,[CompanyId]=source.[CompanyId],[Description]=source.[Description] when NOT MATCHED BY target then insert ([CustomerId] ,[ContractDate] ,[ContractTypeId] ,[CurrencyId] ,[OperationTypeId] ,[CompanyId],[Description]) values(@id ,[ContractDate],[ContractTypeId],[CurrencyId],[OperationTypeId],[CompanyId],[Description]) when NOT MATCHED BY source and target.CustomerId=@Id then update set [DeleteId]=@userId,[DeleteDate]=getdate();
if @@TRANCOUNT>0 commit;
if @operationType=12 goto spSelect; RETURN 0 end
spDelete: begin update [Accounting].[Customer] set [DeleteId]=@userId, [DeleteDate]=GETDATE() where id=@id;
if @operationType=13 goto spSelect; RETURN 0 end
spSelect: select c.[Id] ,c.[CustomerName] ,c.[ParentId] ,c.TypeId ,c.TypeName ,c.[CustomerLeader] ,c.[Voen] ,c.[CurrencyId] ,c.[WorkTypeId] ,c.[WorkTypeName] ,c.[BankId] ,c.[BankName] ,c.[BankSwift] ,c.[RegionId] ,c.[RegionName] ,c.[LegalAddress] ,c.[ActualAddress] ,c.[Email] ,c.[Tel] ,c.[Date] ,c.[Description] from [Accounting].[VwCustomer] c join [Accounting].[Contract] co on c.id=co.CustomerId group by c.[Id] ,c.[CustomerName] ,c.[ParentId] ,c.TypeId,c.TypeName ,c.[CustomerLeader] ,c.[Voen] ,c.[CurrencyId] ,c.[WorkTypeId] ,c.[WorkTypeName] ,c.[BankId] ,c.[BankName] ,c.[BankSwift] ,c.[RegionId] ,c.[RegionName] ,c.[LegalAddress] ,c.[ActualAddress] ,c.[Email] ,c.[Tel] ,c.[Date] ,c.[Description] select * from [Accounting].[VwCustomer] ; RETURN 0 end

CREATE PROCEDURE [Accounting].[spCustomerContract]
@userId int,
@operationType tinyint,
@id int=null,
@customerName NVARCHAR(200) =NULL, 
@customerLeader NVARCHAR(200) =NULL, 
@workTypeId INT =NULL, 
